INSERT INTO `sl_collection_content` VALUES('737','6','1','1','http://www.iface.com.cn/article/8872/','绣眉：让眉毛更自然2006-05-19','array (\n  \'title\' => \'绣眉：让眉毛更自然\',\n  \'time\' => \'1401428549\',\n  \'content\' => \'		  <P>　　林小姐一直对自己稀稀松松的眉毛不满意，很想纹眉，可一看到同伴们纹过的硬硬的、黑黑的眉型，显得那么呆板，自己就望而却步了。林小姐问现在有什么比这个更好的办法，能让眉更真一些，更自然一些。</P><P>　　\r</p>        \',\n)');
