INSERT INTO `sl_collection_content` VALUES('762','6','1','1','http://www.iface.com.cn/article/7492/','眼部保养小常识2006-03-28','array (\n  \'title\' => \'眼部保养小常识\',\n  \'time\' => \'1401428540\',\n  \'content\' => \'		  <P>　　A ：为什么眼睛需使用特殊保养品？ <BR>&nbsp;&nbsp;&nbsp; <BR>　　眼睛周围的皮肤脆弱且复杂，上下眼睑部位的皮肤分为七层，每一层都比脸部的皮肤更薄，更细。此外，这部位的组织结构松散缺乏油脂，因此易肿胀而产 <BR>生泡眼、\r</p>        \',\n)');
INSERT INTO `sl_collection_content` VALUES('763','6','1','1','http://www.iface.com.cn/article/7491/','眼周皱纹防治2006-03-28','array (\n  \'title\' => \'眼周皱纹防治\',\n  \'time\' => \'1401428540\',\n  \'content\' => \'		  <P>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 人的眼睛，除了看东西，还会传递情感，故称“心灵之窗”。因此，为了给他人留下良好的第一印象，眼睛的保养尤为重要。眼睛四周的皮肤只含有少量的\r</p>        \',\n)');
